import React from 'react';
import "./stylesheets/styles-main.css";
import "./language";
import {toggleLang} from "./language";

export class Button extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            lang: "ENG"
        };
        this.switchLang = this.switchLang.bind(this);
    }

    render() {
        return <div>
            <button onClick={toggleLang} className={"langButton"}>{this.state.lang}</button>
        </div>;
    }
}
